<script>
import scrollToHash from '~/mixins/scrollToHash'
import eventiveEverywhere from '~/mixins/eventiveEverywhere'

export default {
  name: 'Bsf12',
  mixins: [scrollToHash, eventiveEverywhere],
  computed: {
    overflowHidden () {
      if (this.$route && this.$route.path) {
        if (this.$route.path.includes('festival-2023/schedule') || this.$route.path.includes('festival-2023/virtual-guide') || this.$route.path.includes('festival-2023/event-guide') || this.$route.path.includes('festival-2023/film-guide') || this.$route.path.includes('festival-2023/events/')) {
          return false
        }
      }

      return true
    }
  },
  mounted () {
    this.$store.commit('setTheme', 'bsf12')
  },
  created () {
    this.$store.commit('setTheme', 'bsf12')
  },
  methods: {
    handleCloseBio () {
      this.$store.commit('festival/setActiveBio', null)
    }
  }
}
</script>

